using System;  namespace OLiOYouxiCore.OAttributes {     /// <summary>     /// 当满足指定条件时隐藏某字段     /// </summary>     [AttributeUsage(AttributeTargets.Field, AllowMultiple = false, Inherited = true)]     public class HideIfAttribute : ShowIfAttribute     {         /// <summary>         /// 当满足指定条件时隐藏某字段         /// </summary>         /// <param name="condition">条件（同showif）</param>         public HideIfAttribute(string condition)             : base(condition)         {             Reversed = true;         }          /// <summary>         /// 当满足指定条件时隐藏某字段         /// </summary>         /// <param name="conditionOperator">交并关系</param>         /// <param name="conditions">条件（同showif）</param>         public HideIfAttribute(ConditionOperator conditionOperator, params string[] conditions)             : base(conditionOperator, conditions)         {             Reversed = true;         }     } } 